mapper.insert:
	/*event.insertEvent*/

		SELECT 
			nvl(TO_NUMBER(MAX(event_id)+1),0)
		FROM
			event 

		INSERT INTO
			event
			(
    			event_id
    			,event_name
    			,event_img
    			,event_com
			)
    	VALUES
		(
    		#{e_id}
	  		,#{e_name}
    		,#{e_img, jdbcType=VARCHAR}
    		,#{e_com, jdbcType=VARCHAR}
    	)

mapper.update:
/*event.updateEvent*/
	UPDATE
		event
	SET
		event_name = #{e_name}
		,event_com = #{e_com }
	WHERE
		event_id = #{e_id}

/*event.fileNameUpdate*/
	UPDATE
		event
	SET
		event_img = #{e_img}
	WHERE
		event_id = #{e_id}
