mapper.select:
/*eventDetail.selectEventDetail*/
	SELECT *
	FROM e_detail
	where event_id = #{e_id, jdbcType=INTEGER}

mapper.insert:
	/*eventDetail.insertEventDetail*/

			SELECT 
    				nvl(TO_NUMBER(MAX(e_detail_id)+1),0)
	 			FROM
	 				e_detail 

		INSERT INTO
			e_detail
			(
    			e_detail_id
    			,event_id
    			,patt_id
    			,patt_sort
			)
    	VALUES
		(
    		#{ed_id}
	  		,#{e_id}
	  		,#{p_id}
	  		,#{p_sort}
    	)
